import 'dart:async';import 'package:shared_preferences/shared_preferences.dart';import 'package:weather_app/prefs/preferences_base.dart';class Prefs {  static Future<String> getString() async {    final SharedPreferences prefs = await SharedPreferences.getInstance();    return prefs.getString(Key.LOCATION_STRING) ?? 'Bandung';  }  static Future setString( String value) async {    final SharedPreferences prefs = await SharedPreferences.getInstance();    return prefs.setString(Key.LOCATION_STRING, value);  }}class Key {  static const String LOCATION_STRING = 'LOCATION_STRING';}