import 'dart:async';import 'package:shared_preferences/shared_preferences.dart';class Prefs {  static Future<String> getString() async {    final SharedPreferences prefs = await SharedPreferences.getInstance();    return prefs.getString(Key.LOCATION_STRING) ?? '';  }  static Future setString( String value) async {    final SharedPreferences prefs = await SharedPreferences.getInstance();    return prefs.setString(Key.LOCATION_STRING, value);  }}class Key {  static const String LOCATION_STRING = 'LOCATION_STRING';}