import 'dart:async';import 'package:shared_preferences/shared_preferences.dart';class PreferencesHelper {  static Future<String> getString(String key) async {    final SharedPreferences prefs = await SharedPreferences.getInstance();    return prefs.getString(key) ?? 'Bandung';  }  static Future setString(String key, String value) async {    final SharedPreferences prefs = await SharedPreferences.getInstance();    return prefs.setString(key, value);  }}